import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { delProduct, getQuantity, postOrderProduct } from "../../Redux/reducers/productReducer";

export default function Cart() {
  const { arrProductCart, count ,arrLogin,arrProductid,postOrder} = useSelector((state) => state.productReducer);
  console.log(postOrder)
  console.log(arrLogin)
  const dispatch=useDispatch()
  const [order,setOrder]=useState({
    orderDetail: [
    {
      productId: "",
      quantity: 0
    }
  ],
  email: ""})
  const [quantity,setQuantity]=useState(count===""?1:count)
  const handleCountUp=()=>{
    setQuantity(quantity+1)
    
   }
const handleCountDown=()=>{
    setQuantity(quantity-1)
    if(quantity<0){
        quantity=0
    }
  }
  const handleDeleteProduct=()=>{
    const action=delProduct(arrProductCart)
    dispatch(action)
  }
  const handleOrder=()=>{
    order.orderDetail.map((item)=>{
      item.productId=arrProductid.id
      item.quantity=count
    })
    order.email=arrLogin.email
    const action=postOrderProduct(order)
    dispatch(action)
    console.log(order)
    console.log(arrLogin)
  }
  // const getQuantityProd=()=>{
  //   const action=getQuantity(order)
  //   dispatch(action)
  // }
  useEffect(()=>{
    handleOrder()
    // getQuantityProd()
  },[])
  return (
    <div>
      <div className="container">
        
        <div className="row">
          <div className="col-12 col-sm-8 col-md-8">
            
              {arrProductCart?.map((item,index)=>{
                return(
                  <div className="d-flex">
                    <div className="imgProduct">
                        <img src={item.image} className="w-25" alt="" />
                    </div>
                    <div className="nameProd">
                      <span>{item.name}</span>
                      <p>{item.price}$</p>
                      <span className='d-flex'>
                             <button type="button" className="btn fs-6 text-white p-0" style={{background:" linear-gradient(180deg, #6181F3 0%, #7C97F5 99.48%)",width:"20px",height:"20px",lineHeight:"6px"
}} onClick={handleCountDown} >-</button>

                            <p className='fs-6 mx-2'>{quantity===""?1:quantity}</p>
                            <button type="button" className="btn fs-6 text-white text-center p-0 " style={{background:" linear-gradient(180deg, #6181F3 0%, #7C97F5 99.48%)",width:"20px",height:"20px",lineHeight:"6px"
}} onClick={handleCountUp}>+</button>
                        </span>
                        <span className="text-danger cursor-pointer border border-danger px-2 bg-danger text-white" onClick={handleDeleteProduct}>X</span>
                    </div>
                  </div>
                  
                )
              })}
          
          </div>
          <div className="col-12 col-sm-4 col-md-4"></div>
        </div>

          
      </div>
    </div>
  );
}
