import axios from "axios";

export const httpShoe=axios.create({
    baseURL:"https://shop.cyberlearn.vn/",
    timeout:30000
})
export const TOKEN = 'accessToken';
export const USER_LOGIN = 'userLogin';
httpShoe.interceptors.request.use((config)=>{
    return config
},err=>{
    return Promise.reject(err)
})
export const { getStoreJson, setStoreJson, getStore, setStore } = {
    getStoreJson: (name) => {
        if (localStorage.getItem(name)) {
            const strResult = localStorage.getItem(name);
            return JSON.parse(strResult);
        }
        return undefined;
    },
    setStoreJson: (name, data) => {
        const strJSON = JSON.stringify(data);
        localStorage.setItem(name, strJSON);
    },
    getStore: (name) => {
        return localStorage.getItem(name);
    },
    setStore: (name, data) => {
        localStorage.setItem(name, data);
    }
}
